CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;


DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20240408120015_startpoint') THEN
    CREATE TABLE "Adverts" (
        "AdvertId" integer GENERATED BY DEFAULT AS IDENTITY,
        "AdvertName" text NOT NULL,
        "AdvertKilo" integer NOT NULL,
        "AdvertDescription" text NOT NULL,
        "AdvertDate" timestamp with time zone NULL,
        "RestaurantId" integer NOT NULL,
        CONSTRAINT "PK_Adverts" PRIMARY KEY ("AdvertId")
    );
    END IF;
END $EF$;

